mapper.select:
select bootstrap.id ,bootstrap.iaas_type ,bootstrap.bootstrap_config_name ,bootstrap.network_config_name ,bootstrap.cpi_config_name ,bootstrap.default_config_name ,bootstrap.resource_config_name ,bootstrap.deployment_file ,bootstrap.deploy_status ,bootstrap.deploy_log ,cpi.iaas_config_id ,resource.stemcell_name ,resource.instance_type ,resource.vm_password ,bootstrap.create_user_id ,bootstrap.create_date ,bootstrap.update_user_id ,bootstrap.update_date ,network.subnet_id ,network.private_static_ip ,network.subnet_range ,network.subnet_gateway ,network.subnet_dns ,network.public_static_ip ,defaultInfo.deployment_name ,defaultInfo.director_name ,defaultInfo.ntp ,defaultInfo.credential_key_name ,defaultInfo.boshRelease ,defaultInfo.bosh_cpi_release ,defaultInfo.bosh_bpm_release ,defaultInfo.uaa_release ,defaultInfo.os_conf_release ,defaultInfo.cred_hub_release ,defaultInfo.enable_snapshots ,defaultInfo.snapshot_schedule ,defaultInfo.paasta_monitoring_use ,defaultInfo.syslog_release ,defaultInfo.syslog_address ,defaultInfo.syslog_port ,defaultInfo.syslog_transport ,defaultInfo.metric_url ,defaultInfo.paasta_monitoring_release ,config.iaas_config_alias as iaas_config_alias ,config.id as iaas_config_id ,config.account_id as account_id ,config.common_security_group as common_security_group ,config.common_region as common_region ,config.common_keypair_name as common_keypair_name ,config.common_keypair_path as common_keypair_path ,config.common_availability_zone as common_availability_zone ,account.id as iaas_account_id ,ifnull(account.common_access_endpoint, "") as common_access_endpoint ,ifnull(account.common_access_user, "") as common_access_user ,ifnull(account.common_access_secret, "") as common_access_secret ,ifnull(account.common_tenant, "") as common_tenant ,ifnull(account.common_project, "") as common_project ,ifnull(account.openstack_keystone_version, "") as openstack_keystone_version ,ifnull(account.openstack_domain, "") as openstack_domain from ieda_bootstrap_config as bootstrap Left join ieda_bootstrap_cpi_config as cpi on bootstrap.cpi_config_name = cpi.cpi_name Left join ieda_bootstrap_network_config as network on bootstrap.network_config_name = network.network_config_name Left join ieda_bootstrap_resource_config as resource on bootstrap.resource_config_name = resource.resource_config_name Left join ieda_bootstrap_default_config as defaultInfo on bootstrap.default_config_name = defaultInfo.default_config_name Left join ieda_iaas_config as config on cpi.iaas_config_id = config.id Left join ieda_iaas_account as account on config.account_id = account.id
where deploy_status is null or deploy_status = '';
where deploy_status is not null or deploy_status != '';

mapper.update:
Update ieda_bootstrap_config set
    iaas_type = #{vo.iaasType}
    ,bootstrap_config_name = #{vo.bootstrapConfigName}
    ,network_config_name = #{vo.networkConfigInfo}
    ,cpi_config_name = #{vo.cpiConfigInfo}
    ,default_config_name = #{vo.defaultConfigInfo}
    ,resource_config_name = #{vo.resourceConfigInfo}
    ,deploy_log = #{vo.deployLog}
    ,deploy_status = #{vo.deployStatus}
    ,deployment_file = #{vo.deploymentFile}
    ,update_user_id = #{vo.updateUserId}
    ,update_date = now()
WHERE
id=#{vo.id}

mapper.select:
Select count(if(snapshot_schedule='',null,snapshot_schedule)) as count   
  From ieda_hybrid_bootstrap 
Where public_static_ip = #{director.directorUrl}
   And director_name = (
       Select director_name 
         From ieda_director_config 
        Where director_name = #{director.directorName} 
          And director_cpi = #{director.directorCpi}
          And director_url = #{director.directorUrl}
   )
