mapper.select:
select hbCfDeployment.id ,hbCfDeployment.iaas_type ,hbCfDeployment.cf_deployment_config_name ,hbCfDeployment.network_config_name ,hbCfDeployment.default_config_name ,hbCfDeployment.resource_config_name ,hbCfDeployment.instance_config_name ,hbCfDeployment.credential_config_name ,hbCfDeployment.cloud_config_file ,hbCfDeployment.deploy_status ,hbCfDeployment.task_id ,hbCfDeployment.create_user_id ,hbCfDeployment.update_user_id ,cfdefault.default_config_name ,cfdefault.cf_deployment_version ,cfdefault.domain ,cfdefault.domain_organization ,cfdefault.cf_db_type ,cfdefault.inception_os_user_name ,cfdefault.cf_admin_password ,cfdefault.portal_domain ,cfdefault.paasta_monitoring_use ,cfdefault.metric_url ,cfdefault.syslog_address ,cfdefault.syslog_port ,cfdefault.syslog_custom_rule ,cfdefault.syslog_fallback_servers ,cfnetwork.network_name ,cfnetwork.public_static_ip ,cfnetwork.subnet_reserved_ip ,cfnetwork.subnet_static_ip ,cfnetwork.subnet_id_1 ,cfnetwork.security_group_1 ,cfnetwork.subnet_range_1 ,cfnetwork.subnet_gateway_1 ,cfnetwork.subnet_dns_1 ,cfnetwork.subnet_reserved_from_1 ,cfnetwork.subnet_reserved_to_1 ,cfnetwork.subnet_static_from_1 ,cfnetwork.subnet_static_to_1 ,cfnetwork.availability_zone_1 ,cfnetwork.subnet_id_2 ,cfnetwork.security_group_2 ,cfnetwork.subnet_range_2 ,cfnetwork.subnet_gateway_2 ,cfnetwork.subnet_dns_2 ,cfnetwork.subnet_reserved_from_2 ,cfnetwork.subnet_reserved_to_2 ,cfnetwork.subnet_static_from_2 ,cfnetwork.subnet_static_to_2 ,cfnetwork.availability_zone_2 ,cfcredential.credential_config_name ,cfcredential.domain ,cfcredential.iaas_type ,cfcredential.domain ,cfcredential.country_code ,cfcredential.city ,cfcredential.company ,cfcredential.job_title ,cfcredential.key_file_name ,cfcredential.release_name ,cfcredential.release_version ,cfcredential.email_address ,cfresource.resource_config_name ,cfresource.stemcell_name ,cfresource.stemcell_version ,cfresource.instance_type_s ,cfresource.instance_type_m ,cfresource.instance_type_l ,cfresource.director_id ,cfinstance.instance_config_name ,cfinstance.iaas_type ,cfinstance.adapter ,cfinstance.api ,cfinstance.cc_worker ,cfinstance.consul ,cfinstance.the_database ,cfinstance.diego_api ,cfinstance.diego_cell ,cfinstance.doppler ,cfinstance.haproxy ,cfinstance.log_api ,cfinstance.nats ,cfinstance.router ,cfinstance.singleton_blobstore ,cfinstance.tcp_router ,cfinstance.uaa ,cfinstance.scheduler from ieda_hb_cfDeployment as hbCfDeployment Left join ieda_hb_cfDeployment_default_config as cfdefault on hbCfDeployment.default_config_name = cfdefault.default_config_name Left Join ieda_hb_cfDeployment_network_config as cfnetwork on hbCfDeployment.network_config_name = cfnetwork.network_name Left Join ieda_hb_cfDeployment_credential_config as cfcredential on hbCfDeployment.credential_config_name = cfcredential.credential_config_name Left Join ieda_hb_cfDeployment_resource_config as cfresource on hbCfDeployment.resource_config_name = cfresource.resource_config_name Left Join ieda_hb_cfDeployment_instance_config as cfinstance on hbCfDeployment.instance_config_name = cfinstance.instance_config_name
where deploy_status is null or hbCfDeployment.deploy_status = '';
where hbCfDeployment.deploy_status is not null or hbCfDeployment.deploy_status != '';

mapper.update:
update ieda_hb_cfDeployment set
cf_deployment_config_name = #{vo.cfDeploymentConfigName}
    ,network_config_name = #{vo.networkConfigInfo}
    ,default_config_name = #{vo.defaultConfigInfo}
    ,resource_config_name = #{vo.resourceConfigInfo}
    ,instance_config_name = #{vo.instanceConfigInfo}
    ,credential_config_name = #{vo.credentialConfigInfo}
    ,cloud_config_file = #{vo.cloudConfigFile}
    ,deploy_status = #{vo.deployStatus}
    ,task_id = #{vo.taskId}
    ,create_user_id = #{vo.createUserId}
    ,create_date = now()
    ,update_user_id = #{vo.updateUserId}
    ,update_date = now()
where id = #{vo.id}
